/// BLS12_377 PAIRING
pub fn bls12_377_pairing() {
    // vec![sa,b,-sb,a]
    let input = hex::decode(
                "").unwrap();

    // e(sa, b) = e(sb, a)
    // e(sa, b) * e(-sb, a) = 1
    assert!(Bls12_377::pairings(&input[..]).expect("pairings failed"));
    // println!("test pairings{} success!", i + 1);
}

/// Pariing Circle
pub fn bls12_377_pairing_six() {
    // test pairings
    bls12_377_pairing();

    // check pairings
    {
        // hex![(a1, b1), (a2, b2), (-a1, b1), (-a2, b2)];
        let pairings_encoded = "";

        let input = hex::decode(pairings_encoded).unwrap();

        // check pairings operation:(a1*b1) * e(a2*b2) * e(-a1*b1) * e(-a2*b2) == 1 return true
        assert!(Bls12_377::pairings(&input[..]).unwrap());
        // println!("test pairings e(a1*b1)*e(a2*b2)*e(-a1*b1)*e(-a2*b2) success!");
    }
}

pub fn bls12_377_verify() {
    let proof = "";
    let vk = "";
    let image = "a4961fec1c734055a80af91b3fd7405a2cdb741e1867aa7f783ca0959a077110";
    let proof_input = hex::decode(proof).unwrap();
    let vk_input = hex::decode(vk).unwrap();
    let image_input = hex::decode(image).unwrap();

    let proof = Proof::<Bls12_377>::deserialize(&*proof_input).expect("Proof deserialize fail:");
    let vk = VerifyingKey::<Bls12_377>::deserialize(&*vk_input).expect("vk deserialize fail");
    let pvk = PreparedVerifyingKey::<Bls12_377>::from(vk);
    let image = Fr::read(&*image_input).unwrap();

    assert!(verify_proof(&pvk, &proof, &[image]).unwrap());
}

