use ark_bls12_377::Bls12_377;
use ark_bls12_381::Bls12_381;
use ark_bn254::Bn254;
use ark_bw6_761::BW6_761;
use ark_cp6_782::CP6_782;
use arkworks::groth16::verify_proof;
use rustc_hex::FromHex;

#[test]
fn test_based_bls12_381_mimc_groth16() {
    let vk_ic:Vec<u8> = FromHex::from_hex("e60f6032ed7f170ba3481c5d99c0890554c808ad380a7c9aefc9e62eeecf0363ecef5e59ba8eaf6dd0af40e08d7cfd024314f90080a1461a207aa3098308149837bc6322a8f4b499671c507203e798f90a9aa8fafaebb806c8708c3207a9331600").unwrap();
    let vk_ic2:Vec<u8> = FromHex::from_hex("ea7f398187aecf2721441218e54645b17acd35bdf65886255e772a58353ef2e25c92cf7f872bea5afca6081d03e6b115e74befd85720c241ee5ef038621a1cc72e329db9aebec0faf480e83cbd9e4b735650ab0cce2e321d64311bd34bca9e0200").unwrap();
    let vk_encode:Vec<u8> = FromHex::from_hex("").unwrap();
    let proof_encode:Vec<u8> = FromHex::from_hex("").unwrap();
    let public_input: Vec<u8> =
        FromHex::from_hex("1d6c7850edbac8a5281ab93d2ed245d47b64f20c21950926d595624b488c291c")
            .unwrap();
    assert!(verify_proof::<Bls12_381>(
        &[&*vk_ic, &*vk_ic2],
        &*vk_encode,
        &*proof_encode,
        &[&*public_input]
    )
    .expect("verify proof fail "));
}

#[test]
fn test_based_bls12_377_mimc_groth16() {
    let vk_ic:Vec<u8> = FromHex::from_hex("71c5a779bb19e0aeb6030768f5001017bbd4edb645306ff1591424c501e7aa8b480fc18d8ed404c893d473afa9b82c01fb3e19bdf85f4fb8acf97d1507b75eb48972c8a10e13f4637f7bee71f89eafc141453e4646c82e188d9ef8cf71fa2f0000").unwrap();
    let vk_ic2:Vec<u8> = FromHex::from_hex("4d0869345487bb4070110fa318b0f408dcffce12304589878797615d8a72cc00b6a1e3f50cc0e59770638640090726013db560d3a19288ecedc60ed9ee0afd50ad3ac96db89d99fc249b85a453ef837e9213f8977ac572d818dc43387678a50100").unwrap();
    let vk_encode:Vec<u8> = FromHex::from_hex("").unwrap();
    let proof_encode:Vec<u8> = FromHex::from_hex("").unwrap();
    let public_input: Vec<u8> =
        FromHex::from_hex("a4961fec1c734055a80af91b3fd7405a2cdb741e1867aa7f783ca0959a077110")
            .unwrap();
    assert!(verify_proof::<Bls12_377>(
        &[&*vk_ic, &*vk_ic2],
        &*vk_encode,
        &*proof_encode,
        &[&public_input[..]]
    )
    .expect("verify proof fail "));
}

